// Generated by CoffeeScript 1.4.0
var displayResult;

displayResult = function(data) {
  $('<a />').attr({
    'href': data.url,
    'target': '_blank'
  }).text(data.url).appendTo('#title');
  $('<span />').text("" + data.count + " users tweet").appendTo('#count');
  return this;
};

$(function() {
  $('#reloadButton').on('click', function() {
    $('#title, #count, #screenshot, #comment').empty();
    return $.getJSON('http://urls.api.twitter.com/1/urls/count.json?url=' + $('#appendedInputButton').val() + '&callback=?', function(data) {
      return displayResult(data);
    });
  });
  return this;
});
